    // Start is called before the first frame update
    void Start()
    {
        ghostKid.SetActive(true);
        int valor = Random.Range(0, 1000);
        ghost.SetBool("Ghost", false); 
        if(valor == 36) 
        {
            ghostKid.SetActive(true);
        }
        else
        {
            ghostKid.SetActive(false);
        }
    }

    private void OnTriggerEnter(Collider other)
    {
        if(other.gameObject.CompareTag("Player"))
        {
            ghost.SetBool("Ghost", true);
        }
    }
